mapper.select:
select userEmail from users
where userEmail = #{userEmail}
limit 1

select * from users
 where userEmail = #{userEmail}
   and userPassword = #{userPassword}
 limit 1

		select ( select files.fileName
			  	 from files
			  	where files.userNo = users.userNo
			) as 'userFiles'
  		from users where users.userName = '#{userName}';
